"use strict";
const HTMLElementImpl = require("jsdom/lib/jsdom/living/nodes/HTMLElement-impl").implementation;
const LinkStyleImpl = require("jsdom/lib/jsdom/living/nodes/LinkStyle-impl").implementation;
const idlUtils = require("jsdom/lib/jsdom/living/generated/utils");
const domSymbolTree = require("jsdom/lib/jsdom/living/helpers/internal-constants").domSymbolTree;
const NODE_TYPE = require("jsdom/lib/jsdom/living/node-type");
const evaluateStylesheet = require("jsdom/lib/jsdom/living/helpers/stylesheets").evaluateStylesheet;
const documentBaseURL = require("jsdom/lib/jsdom/living/helpers/document-base-url").documentBaseURL;

class HTMLStyleElementImpl extends HTMLElementImpl {
  _attach() {
    if (this.type && this.type !== "text/css") {
      return;
    }

    let content = "";
    for (const child of domSymbolTree.childrenIterator(this)) {
      if (child.nodeType === NODE_TYPE.TEXT_NODE) {
        content += child.nodeValue;
      }
    }

    evaluateStylesheet(this, content, this.sheet, documentBaseURL(this._ownerDocument));

    super._attach();
  }
}

idlUtils.mixin(HTMLStyleElementImpl.prototype, LinkStyleImpl.prototype);

module.exports = {
  implementation: HTMLStyleElementImpl
};
